mapper.select:

  		SELECT * FROM
			(SELECT ROWNUM R, B.* FROM
					(SELECT * FROM ITEMS

							ITEM_ID > 0



									AND ITEM_DETAIL LIKE '%'||#{search}||'%'


									AND ITEM_NAME LIKE '%'||#{search}||'%'




						ORDER BY ITEM_ID DESC
					)B
			)
		WHERE R BETWEEN #{startRow} AND #{lastRow}
